package com.kishorebabu.udacity.nanodegree.myappportfolio;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.view.View.OnClickListener;import android.widget.Button;import android.widget.Toast;public class MainActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        Button spotifyStreamer = (Button) findViewById(R.id.spotify_streamer);        Button scoresApp = (Button) findViewById(R.id.scores_app);        Button libraryApp = (Button) findViewById(R.id.library_app);        Button buildItBigger = (Button) findViewById(R.id.build_it_bigger);        Button xyzReader = (Button) findViewById(R.id.xyz_reader);        Button capstoneApp = (Button) findViewById(R.id.capstone_app);        spotifyStreamer.setOnClickListener(new PortfolioButtonClickListener());        scoresApp.setOnClickListener(new PortfolioButtonClickListener());        libraryApp.setOnClickListener(new PortfolioButtonClickListener());        buildItBigger.setOnClickListener(new PortfolioButtonClickListener());        xyzReader.setOnClickListener(new PortfolioButtonClickListener());        capstoneApp.setOnClickListener(new PortfolioButtonClickListener());    }    private class PortfolioButtonClickListener implements OnClickListener {        public static final String TOAST_FORMAT = "This button will launch %s";        @Override        public void onClick(View v) {            switch (v.getId()) {                case R.id.spotify_streamer:                    Toast.makeText(MainActivity.this, String.format(TOAST_FORMAT, getResources().getString(R.string.spotify_streamer)), Toast.LENGTH_SHORT).show();                    break;                case R.id.scores_app:                    Toast.makeText(MainActivity.this, String.format(TOAST_FORMAT, getResources().getString(R.string.scores_app)), Toast.LENGTH_SHORT).show();                    break;                case R.id.library_app:                    Toast.makeText(MainActivity.this, String.format(TOAST_FORMAT, getResources().getString(R.string.library_app)), Toast.LENGTH_SHORT).show();                    break;                case R.id.build_it_bigger:                    Toast.makeText(MainActivity.this, String.format(TOAST_FORMAT, getResources().getString(R.string.build_it_bigger)), Toast.LENGTH_SHORT).show();                    break;                case R.id.xyz_reader:                    Toast.makeText(MainActivity.this, String.format(TOAST_FORMAT, getResources().getString(R.string.xyz_reader)), Toast.LENGTH_SHORT).show();                    break;                case R.id.capstone_app:                    Toast.makeText(MainActivity.this, String.format(TOAST_FORMAT, getResources().getString(R.string.capstone_my_own_app)), Toast.LENGTH_SHORT).show();                    break;            }        }    }}